// src/components/CreateOrder.jsx
import React, { useState } from 'react';
import { Paper, Grid, TextField, MenuItem, Button, Typography, Alert } from '@mui/material';
import axios from '../api/axios';

export default function CreateOrder() {
  const [formData, setFormData] = useState({
    symbol: '',
    orderType: 'Buy',
    quantity: '',
    price: ''
  });
  const [successMsg, setSuccessMsg] = useState('');
  const [errorMsg, setErrorMsg] = useState('');

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSuccessMsg('');
    setErrorMsg('');
    try {
      const token = localStorage.getItem('token');
      await axios.post('/api/orders', formData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSuccessMsg('Order created successfully!');
      setFormData({ symbol: '', orderType: 'Buy', quantity: '', price: '' });
    } catch (err) {
      setErrorMsg(err?.response?.data?.message || 'Order creation failed!');
    }
  };

  return (
    <Paper elevation={3} sx={{ p: 4, mb: 4 }}>
      <Typography variant="h6" gutterBottom>Place a New Order</Typography>
      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={3}>
            <TextField
              fullWidth
              name="symbol"
              label="Stock Symbol"
              value={formData.symbol}
              onChange={handleChange}
              required
            />
          </Grid>
          <Grid item xs={12} sm={3}>
            <TextField
              select
              fullWidth
              name="orderType"
              label="Order Type"
              value={formData.orderType}
              onChange={handleChange}
            >
              <MenuItem value="Buy">Buy</MenuItem>
              <MenuItem value="Sell">Sell</MenuItem>
            </TextField>
          </Grid>
          <Grid item xs={12} sm={3}>
            <TextField
              fullWidth
              name="quantity"
              type="number"
              label="Quantity"
              value={formData.quantity}
              onChange={handleChange}
              required
            />
          </Grid>
          <Grid item xs={12} sm={3}>
            <TextField
              fullWidth
              name="price"
              type="number"
              label="Price per Share"
              value={formData.price}
              onChange={handleChange}
              required
            />
          </Grid>
        </Grid>
        <Button type="submit" variant="contained" sx={{ mt: 3 }}>
          Place Order
        </Button>
        {successMsg && <Alert severity="success" sx={{ mt: 2 }}>{successMsg}</Alert>}
        {errorMsg && <Alert severity="error" sx={{ mt: 2 }}>{errorMsg}</Alert>}
      </form>
    </Paper>
  );
}
