import React, { useState } from 'react';
import axios from '../../api/axios';
import {
  Button,
  Paper,
  Typography,
  Snackbar,
  Alert,
  CircularProgress,
  TextField,
} from '@mui/material';
import TrendingStocks from './TrendingStocks';

// ðŸ“Š Dashboard Component
export const Dashboard = () => {
  return (
    <div>
      <h1 style={{ textAlign: 'center', marginTop: '20px' }}>ðŸ“Š Dashboard</h1>
      <TrendingStocks />
    </div>
  );
};

// ðŸ“ Upload CSV Component
export const CsvStockUploader = () => {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [message, setMessage] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setMessage(null);
  };

  const handleUpload = async () => {
    if (!file) return setMessage({ text: 'Please select a file', severity: 'warning' });

    const formData = new FormData();
    formData.append('file', file);
    setUploading(true);

    try {
      const res = await axios.post('/stocks/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setMessage({ text: `Upload successful: ${res.data.count} stocks processed`, severity: 'success' });
      setFile(null);
    } catch (err) {
      setMessage({ text: err.response?.data?.error || 'Upload failed', severity: 'error' });
    }

    setUploading(false);
  };

  return (
    <Paper sx={{ p: 3, mt: 2 }}>
      <Typography variant="h6" gutterBottom>Upload Stocks CSV</Typography>
      <input type="file" accept=".csv" onChange={handleFileChange} />
      <Button variant="contained" sx={{ mt: 2 }} onClick={handleUpload} disabled={uploading}>
        {uploading ? <CircularProgress size={24} /> : 'Upload & Save'}
      </Button>
      {message && <Alert severity={message.severity} sx={{ mt: 2 }}>{message.text}</Alert>}
    </Paper>
  );
};

// ðŸ‘¤ Customer Form Component
export const CustomerForm = () => {
  const [formData, setFormData] = useState({
    full_name: '',
    email: '',
    phone: '',
    cnic: '',
    customer_no: '',
  });

  const [error, setError] = useState(null);
  const [successOpen, setSuccessOpen] = useState(false);

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/customers', formData);
      setSuccessOpen(true);
      setFormData({ full_name: '', email: '', phone: '', cnic: '', customer_no: '' });
      setError(null);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to create customer');
    }
  };

  return (
    <Paper sx={{ p: 3, mt: 2 }}>
      <Typography variant="h6" gutterBottom>Create Customer</Typography>
      <form onSubmit={handleSubmit} noValidate>
        <TextField fullWidth label="Full Name" name="full_name" value={formData.full_name} onChange={handleChange} margin="normal" required />
        <TextField fullWidth label="Email" name="email" type="email" value={formData.email} onChange={handleChange} margin="normal" required />
        <TextField fullWidth label="Phone Number" name="phone" value={formData.phone} onChange={handleChange} margin="normal" />
        <TextField fullWidth label="CNIC or ID Number" name="cnic" value={formData.cnic} onChange={handleChange} margin="normal" required />
        <TextField fullWidth label="Customer No" name="customer_no" value={formData.customer_no} onChange={handleChange} margin="normal" required />
        <Button type="submit" variant="contained" color="primary" sx={{ mt: 2 }}>Save Customer</Button>
      </form>
      <Snackbar open={successOpen} autoHideDuration={4000} onClose={() => setSuccessOpen(false)}>
        <Alert severity="success" onClose={() => setSuccessOpen(false)}>Customer created successfully!</Alert>
      </Snackbar>
      {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
    </Paper>
  );
};

// ðŸ’¼ Portfolio Form Component
export const PortfolioForm = () => {
  const [formData, setFormData] = useState({
    portfolio_name: '',
    initial_capital: '',
    customer_no: '',
  });

  const [error, setError] = useState(null);
  const [successOpen, setSuccessOpen] = useState(false);

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/portfolios', {
        ...formData,
        initial_capital: parseFloat(formData.initial_capital),
      });
      setSuccessOpen(true);
      setFormData({ portfolio_name: '', initial_capital: '', customer_no: '' });
      setError(null);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to create portfolio');
    }
  };

  return (
    <Paper sx={{ p: 3, mt: 2 }}>
      <Typography variant="h6" gutterBottom>Create Portfolio</Typography>
      <form onSubmit={handleSubmit} noValidate>
        <TextField fullWidth label="Portfolio Name" name="portfolio_name" value={formData.portfolio_name} onChange={handleChange} margin="normal" required />
        <TextField fullWidth label="Initial Capital" type="number" name="initial_capital" value={formData.initial_capital} onChange={handleChange} margin="normal" required />
        <TextField fullWidth label="Customer No" name="customer_no" value={formData.customer_no} onChange={handleChange} margin="normal" required />
        <Button type="submit" variant="contained" color="primary" sx={{ mt: 2 }}>Save Portfolio</Button>
      </form>
      <Snackbar open={successOpen} autoHideDuration={4000} onClose={() => setSuccessOpen(false)}>
        <Alert severity="success" onClose={() => setSuccessOpen(false)}>Portfolio created successfully!</Alert>
      </Snackbar>
      {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
    </Paper>
  );
};
