import React, { useState } from 'react';

const CreateTradeOrder = () => {
  const [message, setMessage] = useState(''); // ✅ This defines both message and setMessage
  const [formData, setFormData] = useState({
    stock_id: '',
    order_type: '',
    order_price: '',
    quantity: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch('http://localhost:5000/api/order', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}` // adjust if needed
        },
        body: JSON.stringify(formData)
      });

      const data = await res.json();

      if (res.ok) {
        setMessage('Order created successfully'); // ✅ This was throwing error before
      } else {
        setMessage(data.message || 'Failed to create order');
      }
    } catch (error) {
      console.error(error);
      setMessage('An error occurred');
    }
  };

  return (
    <div>
      <h2>Create Trade Order</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Stock ID"
          value={formData.stock_id}
          onChange={(e) => setFormData({ ...formData, stock_id: e.target.value })}
          required
        />
        <select
          value={formData.order_type}
          onChange={(e) => setFormData({ ...formData, order_type: e.target.value })}
          required
        >
          <option value="">Select Order Type</option>
          <option value="buy">Buy</option>
          <option value="sell">Sell</option>
        </select>
        <input
          type="number"
          placeholder="Order Price"
          value={formData.order_price}
          onChange={(e) => setFormData({ ...formData, order_price: e.target.value })}
          required
        />
        <input
          type="number"
          placeholder="Quantity"
          value={formData.quantity}
          onChange={(e) => setFormData({ ...formData, quantity: e.target.value })}
          required
        />
        <button type="submit">Place Order</button>
      </form>
    </div>
  );
};

export default CreateTradeOrder;
