import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from '../../api/axios';
import { useNavigate, Link } from 'react-router-dom';
import { TextField, Button, Paper, Typography, Box } from '@mui/material';
const { data } = await axiosInstance.post('/auth/signup', values);
export default function Signup() {
  const navigate = useNavigate();

  const formik = useFormik({
    initialValues: { full_name: '', email: '', password: '' },
    validationSchema: Yup.object({
      full_name: Yup.string().required('Required'),
      email: Yup.string().required('Required').email('Invalid email'),
      password: Yup.string().min(6, 'Must be at least 6 characters').required('Required')
    }),
    onSubmit: async (values, { setSubmitting, setErrors }) => {
      try {
        await axios.post('/auth/signup', values);
        navigate('/login');
      } catch (err) {
        setErrors({ email: err.response?.data?.error || 'Signup failed' });
      }
      setSubmitting(false);
    }
  });

  const inputStyle = {
    '& .MuiInputBase-input': { color: '#e0f7fa' },
    '& .MuiInputLabel-root': { color: '#00ffc6' },
    '& .MuiOutlinedInput-root': {
      '& fieldset': { borderColor: '#00ffc6' },
      '&:hover fieldset': { borderColor: '#00e3b2' },
      '&.Mui-focused fieldset': { borderColor: '#00ffc6' }
    }
  };

  return (
    <Box sx={{
      mt: 8, maxWidth: 400, mx: 'auto',
      background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
      p: 3, borderRadius: 3, boxShadow: '0 0 20px rgba(0,255,170,0.2)'
    }}>
      <Paper sx={{
        p: 4, borderRadius: 3, backgroundColor: '#112D4E',
        boxShadow: '0 0 12px rgba(0,255,170,0.2)'
      }}>
        <Typography variant="h5" sx={{
          color: '#00ffc6', fontWeight: 'bold', textAlign: 'center', mb: 3,
          textShadow: '0 0 4px #00ffc6'
        }}>
          Sign Up
        </Typography>

        <form onSubmit={formik.handleSubmit}>
          <TextField fullWidth label="Full Name" name="full_name" margin="normal"
            sx={inputStyle}
            value={formik.values.full_name} onChange={formik.handleChange} onBlur={formik.handleBlur}
            error={formik.touched.full_name && Boolean(formik.errors.full_name)}
            helperText={formik.touched.full_name && formik.errors.full_name}
          />
          <TextField fullWidth label="Email" name="email" margin="normal"
            sx={inputStyle}
            value={formik.values.email} onChange={formik.handleChange} onBlur={formik.handleBlur}
            error={formik.touched.email && Boolean(formik.errors.email)}
            helperText={formik.touched.email && formik.errors.email}
          />
          <TextField fullWidth label="Password" name="password" type="password" margin="normal"
            sx={inputStyle}
            value={formik.values.password} onChange={formik.handleChange} onBlur={formik.handleBlur}
            error={formik.touched.password && Boolean(formik.errors.password)}
            helperText={formik.touched.password && formik.errors.password}
          />
          <Button fullWidth variant="contained" type="submit"
            disabled={formik.isSubmitting}
            sx={{
              mt: 3, backgroundColor: '#00ffc6', color: '#0f2027', fontWeight: 'bold',
              '&:hover': { backgroundColor: '#00e3b2', boxShadow: '0 0 8px #00e3b2' }
            }}>
            {formik.isSubmitting ? 'Signing up...' : 'Sign Up'}
          </Button>
        </form>

        <Typography variant="body2" sx={{ mt: 3, color: '#e0f7fa', textAlign: 'center' }}>
          Already have an account?{' '}
          <Link to="/login" style={{ color: '#00ffc6', fontWeight: 'bold' }}>
            Login
          </Link>
        </Typography>
      </Paper>
    </Box>
  );
}
