import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from '../../api/axios';
import { useNavigate, Link } from 'react-router-dom';
import { TextField, Button, Paper, Typography, Box } from '@mui/material';

export default function Login() 
{
  const navigate = useNavigate(); const formik = useFormik( { initialValues: { email: '', password: '' }, validationSchema: Yup.object( { email: Yup.string().required('Required').email('Invalid email'), password: Yup.string().min(6, 'Must be at least 6 characters').required('Required') }), onSubmit: async (values, { setSubmitting, setErrors }) => { try { const { data } = await axios.post('/auth/login', values); localStorage.setItem('token', data.token); navigate('/dashboard'); } catch (err) { setErrors({ email: 'Invalid credentials' }); } setSubmitting(false); } });

  return (
    <Box
        sx={
        {
            backgroundColor: '#000',
            minHeight: '100vh',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            px: 2,
        }}
        > 
        <Paper
        elevation={4}
        sx={{
          p: 4,
          backgroundColor: '#112D4E',
          color: '#e0f7fa',
          borderRadius: 3,
          boxShadow: '0 0 12px rgba(0,255,170,0.2)',
        }}
    >
        <Typography
          variant="h5"
          mb={2}
          sx={{
            color: '#00ffc6',
            fontWeight: 'bold',
            textAlign: 'center',
            textShadow: '0 0 4px #00ffc6',
          }}
        >
          Sign In
        </Typography>

        <form onSubmit={formik.handleSubmit}>
          {['email', 'password'].map((field, idx) => (
            <TextField
              key={idx}
              fullWidth
              label={field.charAt(0).toUpperCase() + field.slice(1)}
              name={field}
              type={field === 'password' ? 'password' : 'text'}
              margin="normal"
              value={formik.values[field]}
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              error={formik.touched[field] && Boolean(formik.errors[field])}
              helperText={formik.touched[field] && formik.errors[field]}
              sx={inputStyle}
            />
          ))}

          <Button
            fullWidth
            variant="contained"
            type="submit"
            disabled={formik.isSubmitting}
            sx={{
              mt: 3,
              backgroundColor: '#00ffc6',
              color: '#0f2027',
              fontWeight: 'bold',
              transition: 'all 0.3s ease',
              '&:hover': {
                backgroundColor: '#00e3b2',
                boxShadow: '0 0 8px #00e3b2',
              },
            }}
          >
            Login
          </Button>
        </form>

        <Typography variant="body2" mt={2} sx={{ textAlign: 'center' }}>
          Don't have an account?{' '}
          <Link to="/signup" style={{ color: '#00ffc6', textDecoration: 'underline' }}>
            Sign Up
          </Link>
        </Typography>
      </Paper>
    </Box>
  );
}

const inputStyle = {
  '& .MuiInputBase-input': {
    color: '#e0f7fa',
  },
  '& .MuiInputLabel-root': {
    color: '#00ffc6',
  },
  '& .MuiOutlinedInput-root': {
    '& fieldset': {
      borderColor: '#00ffc6',
    },
    '&:hover fieldset': {
      borderColor: '#00e3b2',
      boxShadow: '0 0 6px #00ffc6',
    },
    '&.Mui-focused fieldset': {
      borderColor: '#00ffc6',
    },
  },
};
