import React, { useState } from 'react';
import axios from 'axios';

export default function OrderForm() {
  const [orderType, setOrderType] = useState('buy');
  const [orderPrice, setOrderPrice] = useState('');
  const [quantity, setQuantity] = useState('');
  const [stockId, setStockId] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem('token');
    if (!token) {
      setMessage('Unauthorized: Please log in first.');
      return;
    }

    const orderData = {
      order_type: orderType,
      order_price: parseFloat(orderPrice),
      quantity: parseInt(quantity),
      stock_id: parseInt(stockId),
    };

    try {
      const response = await axios.post('http://localhost:5000/api/order', orderData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setMessage(`✅ Order created successfully (ID: ${response.data.id})`);
      setOrderType('buy');
      setOrderPrice('');
      setQuantity('');
      setStockId('');
    } catch (error) {
      console.error('Order creation failed:', error);
      setMessage('❌ Failed to create order. Check console for details.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Create Trade Order</h2>

      <label>Stock ID:</label>
      <input
        type="number"
        value={stockId}
        onChange={(e) => setStockId(e.target.value)}
        required
      />

      <label>Order Type:</label>
      <select value={orderType} onChange={(e) => setOrderType(e.target.value)}>
        <option value="buy">Buy</option>
        <option value="sell">Sell</option>
      </select>

      <label>Order Price:</label>
      <input
        type="number"
        value={orderPrice}
        onChange={(e) => setOrderPrice(e.target.value)}
        required
      />

      <label>Quantity:</label>
      <input
        type="number"
        value={quantity}
        onChange={(e) => setQuantity(e.target.value)}
        required
      />

      <button type="submit">Place Order</button>

      {message && <p>{message}</p>}
    </form>
  );
}
