import 
{
  useState,
  useEffect
} from 'react';
import 
{
  Paper,
  Typography,
  TableContainer,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Button,
  Alert
} from '@mui/material';
import axios from '../../api/axios';
export default function MarketWatch() 
{
  const [stocks, setStocks] = useState([]);
  const [watchlist, setWatchlist] = useState(() => 
  {
    const saved = localStorage.getItem('watchlist');
    return saved ? JSON.parse(saved) : [];
  });
  const [message, setMessage] = useState('');
  useEffect(() => 
  {
    fetchStocks();
  }, []);
  const fetchStocks = async () => 
  {
    try 
    {
      const res = await axios.get('/stocks');
      setStocks(res.data);
    }
    catch (err) 
    {
      console.error('Error fetching stocks:', err);
    }
  };
  const addToWatchlist = (symbol) => 
  {
    if (watchlist.includes(symbol)) 
    {
      setMessage(`"${symbol}" is already in your watchlist.`);
      return;
    }
    const updatedList = [...watchlist, symbol];
    setWatchlist(updatedList);
    localStorage.setItem('watchlist', JSON.stringify(updatedList));
    setMessage(`"${symbol}" added to your watchlist.`);
  };
  const getChangeColor = (change) => 
  {
    if (change > 0) return 'green';
    if (change < 0) return 'red';
    return 'inherit';
  };
  return (
    <>
      <Typography
        variant="h5"
        mt={4}
        mb={2}
        sx={{
          textAlign: "center",
          background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
          borderRadius: 2,
          boxShadow: '0 0 12px rgba(0, 255, 170, 0.2)',
          padding: "10px",
          borderRadius: "8px",
          fontWeight: "bold",
          color: "#00ffff", // Neon cyan
          textShadow: `
            0 0 5px #00ffff,
            0 0 10px #00ffff,
            0 0 20px #00ffff,
            0 0 40px #00ffff
          `,
        }}
      >
        ðŸ“ˆ Market Watch
      </Typography>
      {message && (
        <Alert severity="info" sx={{ mb: 2 }}>
          {message}
        </Alert>
      )}
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><strong>Symbol</strong></TableCell>
              <TableCell><strong>Name</strong></TableCell>
              <TableCell><strong>Last Price</strong></TableCell>
              <TableCell><strong>Change %</strong></TableCell>
              <TableCell><strong>Volume</strong></TableCell>
              <TableCell><strong>Watchlist</strong></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            { stocks.map((stock) => 
            {
              const open = parseFloat(stock.openprice);
              const close = parseFloat(stock.closeprice);
              const change = (isNaN(close) ? 0 : close) - (isNaN(open) ? 0 : open);
              const changePercent = open && !isNaN(open)
                ? ((change / open) * 100).toFixed(2)
                : "0.00";
              return (
                <TableRow key={stock.symbol}>
                  <TableCell>{stock.symbol}</TableCell>
                  <TableCell>{stock.companyname || "-"}</TableCell>
                  <TableCell>
                    {!isNaN(close) ? close.toFixed(2) : "-"}
                  </TableCell>
                  <TableCell sx={{ color: getChangeColor(change) }}>
                    {changePercent}%
                  </TableCell>
                  <TableCell>{stock.volume ?? 0}</TableCell>
                  <TableCell>
                    <Button
                      variant="outlined"
                      size="small"
                      disabled={watchlist.includes(stock.symbol)}
                      onClick={() => addToWatchlist(stock.symbol)}
                    >
                      {watchlist.includes(stock.symbol) ? 'Stock Added âœ…' : 'Add to cart ðŸ›’'}
                    </Button>
                  </TableCell>
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </TableContainer>
    </>
  );
}
