import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TrendingStocks = () => {
  const [trendingData, setTrendingData] = useState([]);

  useEffect(() => {
    const fetchTrending = async () => {
      try {
        const { data } = await axios.get("http://localhost:5000/api/stocks");

        // Sort by Open Price (descending) and take top 4
        const topStocks = data
          .filter(stock => stock.openprice !== null) // ensure valid data
          .sort((a, b) => parseFloat(b.openprice) - parseFloat(a.openprice))
          .slice(0, 4);

        setTrendingData(topStocks);
      } catch (err) {
        console.error("Error fetching trending stocks:", err);
      }
    };

    fetchTrending();
  }, []);

  return (
    <div style={styles.container}>
      <h2 style={styles.header}>üî• Trending Stocks</h2>
      <div style={styles.grid}>
        {trendingData.map((stock, index) => (
          <div key={index} style={styles.card}>
            <p><strong>üè≠ Company Name:</strong> {stock.companyname}</p>
            <p><strong>üì≠ Open Price:</strong> {stock.openprice}</p>
            <p><strong>üìà High Price:</strong> {stock.highprice}</p>
            <p><strong>üìâ Low Price:</strong> {stock.lowprice}</p>
            <p><strong>üì™ Close Price:</strong> {stock.closeprice}</p>
            <p><strong>üóëÔ∏è Volume:</strong> {stock.volume}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    maxWidth: '1000px',
    margin: '0 auto',
    fontFamily: 'Arial, sans-serif',
  },
  header: {
    textAlign: 'center',
    marginBottom: '20px',
  },
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(2, 1fr)', // 2 cards per row
    gap: '20px',
  },
  card: {
    backgroundColor: '#000000ff',
    color: 'white',
    borderRadius: '10px',
    padding: '15px',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
    textAlign: 'left',
  },
};

export default TrendingStocks;
