import React from 'react';
const trendingData = 
[
  {
    Date: '4-Jul-25',
    Symbol: 'AICL-JUL',
    Code: '40',
    'Company Name': 'Adamjee Ins.',
    'Open Price': '59.89',
    'High Price': '63.36',
    'Low Price': '58',
    'Close Price': '62.57',
    Volume: '615000',
    'Previous Close': '57.6'
  },
  // add more but with logic from array
];
const TrendingStocks = () => 
{
  return (
    <div style={styles.container}>
      <h2 style={styles.header}>üî• Trending Stocks</h2>
      <div style={styles.grid}>
        {trendingData.map((stock, index) => 
        (
          <div key={index} style={styles.card}>
            <p><strong>üìÜ Date:</strong> {stock.Date}</p>
            <p><strong>üè≠ Company Name:</strong> {stock['Company Name']}</p>
            <p>
              <strong>üì≠ Open Price:</strong>{' '}
              <span style={{ color: parseFloat(stock['Open Price']) > parseFloat(stock['Previous Close']) ? 'white' : 'white' }}>
                {stock['Open Price']}
              </span>
            </p>
            <p>
              <strong>üìà High Price:</strong>{' '}
              <span style={{ color: parseFloat(stock['High Price']) > parseFloat(stock['Previous Close']) ? 'green' : 'green' }}>
                {stock['High Price']}
              </span>
            </p>
            <p>
              <strong>üìâ Low Price:</strong>{' '}
              <span style={{ color: parseFloat(stock['Low Price']) > parseFloat(stock['Previous Close']) ? 'red' : 'red' }}>
                {stock['Low Price']}
              </span>
            </p>
            <p>
              <strong>üì™ Close Price:</strong>{' '}
              <span style={{ color: parseFloat(stock['Close Price']) > parseFloat(stock['Previous Close']) ? 'green' : 'green' }}>
                {stock['Close Price']}
              </span>
            </p>
            <p>
              <strong>üîí Previous Close:</strong>{' '}
                <span style={{ color: parseFloat(stock['Previous Close']) > parseFloat(stock['Previous Close']) ? 'green' : 'green' }}>
                {stock['Previous Close']}
                </span>  
            </p>
            <p>
              <strong>üóëÔ∏è Volume:</strong>{' '}
              <span>
                {stock['Volume']}
              </span>
              </p>
          </div>
        ))}
      </div>
    </div>
  );
};
const styles = 
{
  container: 
  {
    padding: '30px',
    maxWidth: '1000px',
    margin: '0 auto',
    fontFamily: 'Arial, sans-serif',
  },
  header: 
  {
    textAlign: 'center',
    marginBottom: '20px',
  },
  grid: 
  {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
    gap: '20px',
  },
  card: 
  {
    backgroundColor: '#000000ff',
    color: 'white',
    borderRadius: '10px',
    padding: '15px',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
    textAlign: 'left',
  },
};
export default TrendingStocks;
