import React, { useState } from 'react';
import axios from '../../api/'
import { TextField, Button, Paper, Typography, Alert } from '@mui/material';

export default function CreateOrder() {
  const [order, setOrder] = useState({
    symbol: '',
    quantity: '',
    orderType: 'buy', // or 'sell'
    price: '',
  });

  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    setOrder({ ...order, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(null);
    setError(null);
    try {
      const response = await axios.post('/api/order', order);
      setMessage('Order created successfully');
      setOrder({ symbol: '', quantity: '', orderType: 'buy', price: '' });
    } catch (err) {
      setError('Order creation failed: ' + (err.response?.data?.message || err.message));
    }
  };

  return (
    <Paper elevation={3} style={{ padding: 20, maxWidth: 500, margin: 'auto' }}>
      <Typography variant="h6" gutterBottom>
        Create Trade Order
      </Typography>
      <form onSubmit={handleSubmit}>
        <TextField
          label="Stock Symbol"
          name="symbol"
          value={order.symbol}
          onChange={handleChange}
          fullWidth
          required
          margin="normal"
        />
        <TextField
          label="Quantity"
          name="quantity"
          type="number"
          value={order.quantity}
          onChange={handleChange}
          fullWidth
          required
          margin="normal"
        />
        <TextField
          label="Price"
          name="price"
          type="number"
          value={order.price}
          onChange={handleChange}
          fullWidth
          required
          margin="normal"
        />
        <TextField
          label="Order Type"
          name="orderType"
          value={order.orderType}
          onChange={handleChange}
          select
          SelectProps={{ native: true }}
          fullWidth
          required
          margin="normal"
        >
          <option value="buy">Buy</option>
          <option value="sell">Sell</option>
        </TextField>
        <Button type="submit" variant="contained" color="primary" fullWidth>
          Submit Order
        </Button>
      </form>
      {message && <Alert severity="success" style={{ marginTop: 20 }}>{message}</Alert>}
      {error && <Alert severity="error" style={{ marginTop: 20 }}>{error}</Alert>}
    </Paper>
  );
}
