import React, { useState, useEffect } from 'react';
import axios from '../../api/axios';
import {
  Box, Button, MenuItem, TextField, Typography, Alert,
} from '@mui/material';
export const listStocks = async (req, res) => {
  try 
  {
    const stocks = await stocks.findAll(
    {
      attributes: ['id', 'name'] // or 'symbol' if needed
    });
    res.json(stocks);
  } catch (err) {
    res.status(500).json({ error: err.message });
  }
};

export default function CreateOrder()
{
  const [stockList, setStockList] = useState([]);
  const [stockId, setStockId] = useState('');
  const [orderType, setOrderType] = useState('buy');
  const [orderPrice, setOrderPrice] = useState('');
  const [quantity, setQuantity] = useState('');
  const [statusMsg, setStatusMsg] = useState('');
  const [error, setError] = useState('');
  const token = localStorage.getItem('token');
  const handleSubmit = async (e) => 
  {
    e.preventDefault();
    const selectedStock = stockList.find((stock) => stock.id === stockId);
    if (!selectedStock) 
    {
      setError('Stock not found.');
      setStatusMsg('');
      return;
    }
    try 
    {
      const response = await axios.post(
        '/order',
        {
          stock_id: stockId,
          order_type: orderType,
          order_price: parseFloat(orderPrice),
          quantity: parseInt(quantity),
          status: 'pending',
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setStatusMsg('Order placed successfully!');
      setError('');
      setStockId('');
      setOrderPrice('');
      setQuantity('');
    } 
    catch (err) 
    {
      console.error('Order error:', err);
      setError(err.response?.data?.message || 'Failed to place order.');
      setStatusMsg('');
    }
  };
  return (
    <Box sx={{ maxWidth: 400, mx: 'auto', mt: 4 }}>
      <Typography variant="h5" gutterBottom>Create New Order</Typography>
      {statusMsg && <Alert severity="success">{statusMsg}</Alert>}
      {error && <Alert severity="error">{error}</Alert>}
      <form onSubmit={handleSubmit}>
        <TextField
          select
          label="Select Stock"
          fullWidth
          margin="normal"
          value={stockId}
          onChange={(e) => setStockId(e.target.value)}
          required
        >
          {stockList.map((stock) => (
            <MenuItem key={stock.id} value={stock.id}>
              {stock.symbol} - {stock['Company Name']}
            </MenuItem>
          ))}
        </TextField>

        <TextField
          select
          label="Order Type"
          fullWidth
          margin="normal"
          value={orderType}
          onChange={(e) => setOrderType(e.target.value)}
        >
          <MenuItem value="buy">Buy</MenuItem>
          <MenuItem value="sell">Sell</MenuItem>
        </TextField>

        <TextField
          label="Order Price"
          type="number"
          fullWidth
          margin="normal"
          value={orderPrice}
          onChange={(e) => setOrderPrice(e.target.value)}
          required
        />
        <TextField
          label="Quantity"
          type="number"
          fullWidth
          margin="normal"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          required
        />
        <Button variant="contained" type="submit" fullWidth sx={{ mt: 2 }}>
          Submit Order
        </Button>
      </form>
    </Box>
  );
}
