import React, { useState } from 'react';
import axios from '../axios';
import { Paper, Typography, Button, CircularProgress, Alert } from '@mui/material';

export default function CsvStockUploader() {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setMessage(null);
    setError(null);
  };

  const handleUpload = async () => {
    if (!file) {
      setError('Please select a file first');
      return;
    }

    const formData = new FormData();
    formData.append('file', file); // name must match multer: upload.single('file')

    setUploading(true);
    try {
      const response = await axios.post('/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setMessage(response.data.message);
      setError(null);
    } catch (err) {
      setError(err.response?.data?.message || 'Upload failed');
      setMessage(null);
    } finally {
      setUploading(false);
    }
  };

  return (
    <Paper elevation={3} style={{ padding: '20px', width: '400px', margin: '20px auto' }}>
      <Typography variant="h6" gutterBottom>
        Upload CSV File
      </Typography>
      <input type="file" accept=".csv" onChange={handleFileChange} />
      <br /><br />
      <Button variant="contained" color="primary" onClick={handleUpload} disabled={uploading}>
        {uploading ? <CircularProgress size={24} color="inherit" /> : 'Upload'}
      </Button>
      <br /><br />
      {message && <Alert severity="success">{message}</Alert>}
      {error && <Alert severity="error">{error}</Alert>}
    </Paper>
  );
}
