import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from '../../api/axios';
import { useNavigate, Link } from 'react-router-dom';
import { TextField, Button, Paper, Typography, Box } from '@mui/material';

export default function Signup() {
  const navigate = useNavigate();

  const formik = useFormik({
    initialValues: { full_name: '', email: '', password: '' },
    validationSchema: Yup.object({
      full_name: Yup.string().required('Required'),
      email: Yup.string().required('Required').email('Invalid email'),
      password: Yup.string().min(6, 'Must be at least 6 characters').required('Required'),
    }),
    onSubmit: async (values, { setSubmitting, setErrors }) => {
      try {
        await axios.post('/auth/signup', values);
        navigate('/login');
      } catch (err) {
        setErrors({ email: err.response?.data?.error || 'Signup failed' });
      }
      setSubmitting(false);
    },
  });

  return (
    <Box
        sx={{
            backgroundColor: '#000',
            minHeight: '100vh',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            px: 2,
        }}
        > 
        <Paper
        elevation={4}
        sx={{
          p: 4,
          backgroundColor: '#112D4E',
          color: '#e0f7fa',
          borderRadius: 3,
          boxShadow: '0 0 12px rgba(0,255,170,0.25)',
        }}
      >
        <Typography
          variant="h5"
          mb={2}
          sx={{
            color: '#00ffc6',
            fontWeight: 'bold',
            textAlign: 'center',
            textShadow: '0 0 4px #00ffc6',
          }}
        >
          Sign Up
        </Typography>

        <form onSubmit={formik.handleSubmit}>
          {['full_name', 'email', 'password'].map((field, i) => (
            <TextField
              key={i}
              fullWidth
              label={
                field === 'full_name'
                  ? 'Full Name'
                  : field.charAt(0).toUpperCase() + field.slice(1)
              }
              name={field}
              type={field === 'password' ? 'password' : 'text'}
              margin="normal"
              value={formik.values[field]}
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              error={formik.touched[field] && Boolean(formik.errors[field])}
              helperText={formik.touched[field] && formik.errors[field]}
              sx={inputStyle}
            />
          ))}

          <Button
            fullWidth
            variant="contained"
            type="submit"
            disabled={formik.isSubmitting}
            sx={{
              mt: 3,
              backgroundColor: '#00ffc6',
              color: '#0f2027',
              fontWeight: 'bold',
              transition: 'all 0.3s ease',
              '&:hover': {
                backgroundColor: '#00e3b2',
                boxShadow: '0 0 8px #00e3b2',
              },
            }}
          >
            Sign Up
          </Button>
        </form>

        <Typography variant="body2" mt={2} sx={{ textAlign: 'center' }}>
          Already have an account?{' '}
          <Link to="/login" style={{ color: '#00ffc6', textDecoration: 'underline' }}>
            Login
          </Link>
        </Typography>
      </Paper>
    </Box>
  );
}

const inputStyle = {
  '& .MuiInputBase-input': {
    color: '#e0f7fa',
  },
  '& .MuiInputLabel-root': {
    color: '#00ffc6',
  },
  '& .MuiOutlinedInput-root': {
    '& fieldset': {
      borderColor: '#00ffc6',
    },
    '&:hover fieldset': {
      borderColor: '#00e3b2',
      boxShadow: '0 0 6px #00ffc6',
    },
    '&.Mui-focused fieldset': {
      borderColor: '#00ffc6',
    },
  },
};
