import { TextField, Button, Paper, Typography, Box } from '@mui/material';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function Login() {
  const navigate = useNavigate();

  const formik = useFormik({
    initialValues: { email: '', password: '' },
    validationSchema: Yup.object({
      email: Yup.string().required('Required').email('Invalid email'),
      password: Yup.string().min(6, 'Must be at least 6 characters').required('Required'),
    }),
    onSubmit: async (values, { setSubmitting, setErrors }) => {
      try {
        const { data } = await axios.post('/auth/login', values);
        localStorage.setItem('token', data.token);
        navigate('/dashboard');
      } catch (err) {
        console.error('Login error:', err);
        setErrors({ email: 'Invalid credentials' });
      }
      setSubmitting(false);
    },
  });

  const inputStyle = {
    '& .MuiInputBase-input': { color: '#e0f7fa' },
    '& .MuiInputLabel-root': { color: '#00ffc6' },
    '& .MuiOutlinedInput-root': {
      '& fieldset': { borderColor: '#00ffc6' },
      '&:hover fieldset': { borderColor: '#00e3b2' },
      '&.Mui-focused fieldset': { borderColor: '#00ffc6' }
    }
  };

  return (
    <Box sx={{
      height: '100vh', display: 'flex', justifyContent: 'center', alignItems: 'center',
      background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)'
    }}>
      <Paper sx={{
        p: 5, borderRadius: 3, boxShadow: '0 0 20px rgba(0,255,170,0.2)',
        backgroundColor: '#112D4E', width: 400
      }}>
        <Typography variant="h5" sx={{ color: '#00ffc6', fontWeight: 'bold', textAlign: 'center', mb: 3 }}>
          Login
        </Typography>

        <form onSubmit={formik.handleSubmit}>
          <TextField fullWidth label="Email" name="email" type="email" sx={inputStyle}
            value={formik.values.email} onChange={formik.handleChange} onBlur={formik.handleBlur}
            error={formik.touched.email && Boolean(formik.errors.email)}
            helperText={formik.touched.email && formik.errors.email} margin="normal"
          />
          <TextField fullWidth label="Password" name="password" type="password" sx={inputStyle}
            value={formik.values.password} onChange={formik.handleChange} onBlur={formik.handleBlur}
            error={formik.touched.password && Boolean(formik.errors.password)}
            helperText={formik.touched.password && formik.errors.password} margin="normal"
          />
          <Button type="submit" variant="contained" fullWidth disabled={formik.isSubmitting}
            sx={{
              mt: 3, backgroundColor: '#00ffc6', color: '#0f2027', fontWeight: 'bold',
              '&:hover': { backgroundColor: '#00e3b2', boxShadow: '0 0 8px #00e3b2' }
            }}>
            {formik.isSubmitting ? 'Logging in...' : 'Login'}
          </Button>
        </form>
      </Paper>
    </Box>
  );
}
