import React, { useState } from 'react';
import { fetchStockData } from './stockService';
const StockCard = () => 
{
  const [symbol, setSymbol] = useState('');
  const [stock, setStock] = useState(null);
  const [error, setError] = useState('');
  const handleSearch = async () => 
  {
    try 
    {
      const data = await fetchStockData(symbol);
      if (data && data['01. symbol']) 
      {
        setStock(data);
        setError('');
      } 
      else 
      {
        setStock(null);
        setError('No data found for symbol.');
      }
    } catch (err) {
      setError('Something went wrong.');
    }
  };

  return (
    <div style={styles.wrapper}>
      <div style={styles.cardContainer}>
        <h2 style={styles.header}>ðŸ“ˆ Market Watch</h2>
        <div style={styles.searchSection}>
          <input
            type="text"
            value={symbol}
            onChange={(e) => setSymbol(e.target.value.toUpperCase())}
            placeholder="Enter stock symbol (e.g. AAPL)"
            style={styles.input}
          />
          <button onClick={handleSearch} style={styles.button}>Search</button>
        </div>
        {error && <p style={styles.error}>{error}</p>}
        {stock && (
          <div style={styles.card}>
            <h3 style={styles.symbol}>{stock['01. symbol']}</h3>
            <p><strong>Price:</strong> ${stock['05. price']}</p>
            <p>
              <strong>Change:</strong>{' '}
              <span style={{ color: parseFloat(stock['09. change']) >= 0 ? '#00ff7f' : '#ff4d4d' }}>
                {stock['09. change']} ({stock['10. change percent']})
              </span>
            </p>
            <p><strong>High:</strong> ${stock['03. high']}</p>
            <p><strong>Low:</strong> ${stock['04. low']}</p>
          </div>
        )}
      </div>
    </div>
  );
};

const styles = {
  wrapper: {
    background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
    minHeight: '100vh',
    paddingTop: '40px',
    color: '#e0f7fa',
    fontFamily: 'Roboto, sans-serif',
  },
  cardContainer: {
    backgroundColor: '#112D4E',
    padding: '30px',
    maxWidth: '600px',
    margin: '0 auto',
    borderRadius: '16px',
    boxShadow: '0 0 20px rgba(0, 255, 170, 0.25)',
  },
  header: {
    textAlign: 'center',
    marginBottom: '25px',
    color: '#00ffc6',
    textShadow: '0 0 5px #00ffc6',
  },
  searchSection: {
    display: 'flex',
    gap: '10px',
    justifyContent: 'center',
    marginBottom: '20px',
  },
  input: {
    padding: '10px',
    fontSize: '16px',
    width: '60%',
    borderRadius: '8px',
    border: '1px solid #00ffc6',
    backgroundColor: '#0f3460',
    color: '#e0f7fa',
  },
  button: {
    padding: '10px 15px',
    fontSize: '16px',
    backgroundColor: '#00ffc6',
    color: '#0f2027',
    fontWeight: 'bold',
    borderRadius: '8px',
    border: 'none',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
  },
  card: {
    marginTop: '20px',
    backgroundColor: '#393E46',
    borderRadius: '12px',
    padding: '20px',
    boxShadow: '0 0 12px rgba(0,255,170,0.2)',
  },
  symbol: {
    fontSize: '24px',
    marginBottom: '12px',
    color: '#00ffc6',
  },
  error: {
    color: '#ff4d4d',
    textAlign: 'center',
    fontWeight: 'bold',
  },
};

export default StockCard;
