import React from 'react';
const trendingData = 
[
  {
    symbol: 'AAPL',
    price: 198.23,
    change: +1.45,
    percent: '+0.73%',
    high: 200.45,
    low: 195.10,
  },
  {
    symbol: 'MSFT',
    price: 345.10,
    change: -2.12,
    percent: '-0.61%',
    high: 348.00,
    low: 340.75,
  },
  {
    symbol: 'GOOGL',
    price: 2855.30,
    change: +5.22,
    percent: '+0.18%',
    high: 2870.00,
    low: 2820.50,
  },
  {
    symbol: 'AMZN',
    price: 129.80,
    change: +0.90,
    percent: '+0.70%',
    high: 132.00,
    low: 127.20,
  },
];
const TrendingStocks = () => 
{
  return (
    <div style={styles.container}>
      <h2 style={styles.header}>ðŸ”¥ Trending Stocks</h2>
      <div style={styles.grid}>
        {trendingData.map((stock) => 
        (
          <div key={stock.symbol} style={styles.card}>
            <h3>{stock.symbol}</h3>
            <p><strong>Price:</strong> ${stock.price}</p>
            <p>
              <strong>Change:</strong>{' '}
              <span style={{ color: stock.change >= 0 ? 'green' : 'red' }}>
                {stock.change} ({stock.percent})
              </span>
            </p>
            <p><strong>High:</strong> ${stock.high}</p>
            <p><strong>Low:</strong> ${stock.low}</p>
          </div>
        ))}
      </div>
    </div>
  );
};
const styles = 
{
  container: 
  {
    padding: '30px',
    maxWidth: '1000px',
    margin: '0 auto',
    fontFamily: 'Arial, sans-serif',
  },
  header: 
  {
    textAlign: 'center',
    marginBottom: '20px',
  },
  grid: 
  {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(220px, 1fr))',
    gap: '20px',
  },
  card: 
  {
    backgroundColor: '#000000ff',
    borderRadius: '10px',
    padding: '15px',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
    textAlign: 'left',
  },
};
export default TrendingStocks;
