export default function CreateTradeOrder({ onCreated }) 
{
  const [formData, setFormData] = useState(
  {
    user_id: '', stock_id: '', order_type: 'Buy', order_price: '', quantity: '', status: 'Open'
  });
  const handleChange = (e) =>
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  const handleSubmit = async (e) => 
  {
    e.preventDefault();
    await axios.post('/tradeorders', formData);
    onCreated?.();
    setFormData({ user_id: '', stock_id: '', order_type: 'Buy', order_price: '', quantity: '', status: 'Open' });
  };
  return (
    <Paper sx={{
      p: 4, backgroundColor: '#112D4E', color: '#e0f7fa', borderRadius: 3,
      boxShadow: '0 0 12px rgba(0,255,170,0.2)', maxWidth: 400, mx: 'auto', mt: 4
    }}>
      <Typography variant="h6" sx={{ color: '#00ffc6', fontWeight: 'bold', mb: 2, textAlign: 'center' }}>
        Place Trade Order
      </Typography>

      <form onSubmit={handleSubmit}>
        {['user_id', 'stock_id', 'quantity', 'order_price'].map((field) => (
          <TextField key={field} fullWidth name={field} label={field.replace('_', ' ').toUpperCase()}
            value={formData[field]} onChange={handleChange} margin="normal" sx={inputStyle} required />
        ))}
        <TextField select fullWidth name="order_type" label="Order Type"
          value={formData.order_type} onChange={handleChange} margin="normal" sx={inputStyle}>
          <MenuItem value="Buy">Buy</MenuItem>
          <MenuItem value="Sell">Sell</MenuItem>
        </TextField>
        <TextField select fullWidth name="status" label="Status"
          value={formData.status} onChange={handleChange} margin="normal" sx={inputStyle}>
          <MenuItem value="Open">Open</MenuItem>
          <MenuItem value="Completed">Completed</MenuItem>
          <MenuItem value="Cancelled">Cancelled</MenuItem>
        </TextField>

        <Button type="submit" fullWidth variant="contained" sx={{
          mt: 3, backgroundColor: '#00ffc6', color: '#0f2027', fontWeight: 'bold',
          '&:hover': { backgroundColor: '#00e3b2', boxShadow: '0 0 8px #00e3b2' }
        }}>
          Submit
        </Button>
      </form>
    </Paper>
  );
}

const inputStyle = {
  '& .MuiInputBase-input': { color: '#e0f7fa' },
  '& .MuiInputLabel-root': { color: '#00ffc6' },
  '& .MuiOutlinedInput-root': {
    '& fieldset': { borderColor: '#00ffc6' },
    '&:hover fieldset': { borderColor: '#00e3b2', boxShadow: '0 0 6px #00ffc6' },
    '&.Mui-focused fieldset': { borderColor: '#00ffc6' }
  }
};
