import React from 'react';
import CsvStockUploader from ''
const TrendingStocks = ({ stockData }) => {
  const sortedStocks = [...stockData].sort((a, b) => b.ClosePrice - a.ClosePrice);
  const trendingData = sortedStocks.slice(0, 4);

  return (
    <div style={styles.container}>
      <h2 style={styles.header}>ðŸ”¥ Trending Stocks</h2>
      <div style={styles.grid}>
        {trendingData.map((stock) => (
          <div key={stock.Symbol} style={styles.card}>
            <h3>{stock.Symbol}</h3>
            <p><strong>Price:</strong> ${stock.ClosePrice}</p>
            <p>
              <strong>Change:</strong>{' '}
              <span style={{ color: stock.Change >= 0 ? 'green' : 'red' }}>
                {stock.Change} ({stock.PercentChange})
              </span>
            </p>
            <p><strong>High:</strong> ${stock.HighPrice}</p>
            <p><strong>Low:</strong> ${stock.LowPrice}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '30px',
    maxWidth: '1000px',
    margin: '0 auto',
    fontFamily: 'Arial, sans-serif',
  },
  header: {
    textAlign: 'center',
    marginBottom: '20px',
  },
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(220px, 1fr))',
    gap: '20px',
  },
  card: {
    backgroundColor: '#000000ff',
    borderRadius: '10px',
    padding: '15px',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
    textAlign: 'left',
    color: 'white',
  },
};

export default TrendingStocks;
