import React, { useState } from 'react';
import { fetchStockData } from './stockService';

function StockCard() {
  const [symbol, setSymbol] = useState('');
  const [stock, setStock] = useState(null);

  const handleSearch = async () => {
    const data = await fetchStockData(symbol);
    setStock(data);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>ðŸ“ˆ Market Watch</h2>
      <input
        type="text"
        value={symbol}
        onChange={(e) => setSymbol(e.target.value.toUpperCase())}
        placeholder="Enter stock symbol (e.g. AAPL)"
      />
      <button onClick={handleSearch}>Get Stock</button>

      {stock && (
        <div style={{ marginTop: '20px' }}>
          <h3>{stock['01. symbol']}</h3>
          <p>Price: ${stock['05. price']}</p>
          <p>Change: {stock['09. change']} ({stock['10. change percent']})</p>
          <p>High: ${stock['03. high']}</p>
          <p>Low: ${stock['04. low']}</p>
        </div>
      )}
    </div>
  );
}

export default StockCard;
