export default function TradeOrderTable() 
{
  const [orders, setOrders] = useState([]);
  useEffect(() => 
    {
        axios.get('/tradeorders').then((res) => setOrders(res.data));
    }, []);
    return (
    <Box sx={
        {
            background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
            p: 3, borderRadius: 3, boxShadow: '0 0 12px rgba(0,255,170,0.2)', color: '#e0f7fa'
        }}>
        <Typography variant="h5" sx={{
        color: '#00ffc6', fontWeight: 'bold', textShadow: '0 0 4px #00ffc6'
        }}>
        Trade Orders</Typography>
        <Paper sx={{ backgroundColor: '#112D4E', borderRadius: 3, mt: 2 }}>
            <Table>
                <TableHead sx={{ backgroundColor: '#0F3460' }}>
                    <TableRow>
                        {['Symbol', 'Qty', 'Type', 'Price', 'Status', 'Created'].map((col) => (
                        <TableCell key={col} sx={{ color: '#00ffc6', fontWeight: 'bold' }}>
                            {col}
                        </TableCell>
                        ))}
                    </TableRow>
                </TableHead>
                <TableBody>
                    {orders.map((order) => 
                    (
                        <TableRow key={order.id} sx={
                        {
                            '&:hover': { backgroundColor: '#1B1F3B', cursor: 'pointer' }
                        }}>
                        <TableCell>{order.stock_id}</TableCell>
                        <TableCell>{order.quantity}</TableCell>
                        <TableCell>{order.order_type}</TableCell>
                        <TableCell>${order.order_price}</TableCell>
                        <TableCell>{order.status}</TableCell>
                        <TableCell>{new Date(order.created_at).toLocaleString()}</TableCell>
                        </TableRow>
                        ))
                    }
            </TableBody>
            </Table>
        </Paper>
    </Box>
  );
}
