import React, { useEffect, useState } from 'react';
import axios from '../../api/axios';
import {
  Paper, Typography, TextField, MenuItem,
  Button, CircularProgress, Alert
} from '@mui/material';
const OrderScreen = () => 
{
  const [stocks, setStocks] = useState([]);
  const [selectedStock, setSelectedStock] = useState('');
  const [orderType, setOrderType] = useState('buy');
  const [orderPrice, setOrderPrice] = useState('');
  const [quantity, setQuantity] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);
  localStorage.setItem('token', res.data.token);
  useEffect(() => {
    axios.get('/api/stocks/all', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => setStockOptions(res.data))
    .catch(err => console.error(err));
  }, []);

  // ✅ Submit order
  const handleSubmit = async () => {
    localStorage.setItem('token', res.data.token);
    setLoading(true);
    try {
      const res = await axios.post('/orders', {
        stock_id: selectedStock,
        order_type: orderType,
        order_price: parseFloat(orderPrice),
        quantity: parseInt(quantity),
      });
      setMessage('Order placed successfully');
      setError(null);
    } catch (err) {
      setError('Failed to place order');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Paper sx={{ p: 4, maxWidth: 500, margin: 'auto', mt: 5 }}>
      <Typography variant="h5">Place New Order</Typography>

      {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
      {message && <Alert severity="success" sx={{ mt: 2 }}>{message}</Alert>}

      <TextField
        fullWidth
        select
        label="Select Stock"
        value={selectedStock}
        onChange={e => setSelectedStock(e.target.value)}
        margin="normal"
      >
        {stocks.map(stock => (
          <MenuItem key={stock.id} value={stock.id}>
            {stock.name}
          </MenuItem>
        ))}
      </TextField>

      <TextField
        fullWidth
        select
        label="Order Type"
        value={orderType}
        onChange={e => setOrderType(e.target.value)}
        margin="normal"
      >
        <MenuItem value="buy">Buy</MenuItem>
        <MenuItem value="sell">Sell</MenuItem>
      </TextField>

      <TextField
        fullWidth
        label="Order Price"
        type="number"
        value={orderPrice}
        onChange={e => setOrderPrice(e.target.value)}
        margin="normal"
      />

      <TextField
        fullWidth
        label="Quantity"
        type="number"
        value={quantity}
        onChange={e => setQuantity(e.target.value)}
        margin="normal"
      />

      <Button
        fullWidth
        variant="contained"
        color="primary"
        sx={{ mt: 2 }}
        onClick={handleSubmit}
        disabled={loading}
      >
        {loading ? <CircularProgress size={24} /> : 'Place Order'}
      </Button>
    </Paper>
  );
};

export default OrderScreen;
