// OrderForm.jsx

import React, { useState, useEffect } from 'react';
import axios from '../../api/axios'; // Adjust based on your folder structure
import { TextField, Button, MenuItem, Typography, Paper } from '@mui/material';

export default function OrderForm() {
  const [stocks, setStocks] = useState([]);
  const [stockId, setStockId] = useState('');
  const [orderType, setOrderType] = useState('buy');
  const [orderPrice, setOrderPrice] = useState('');
  const [quantity, setQuantity] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    axios.get('/api/stocks')
      .then((res) => setStocks(res.data))
      .catch((err) => console.error('Error fetching stocks:', err));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const orderData = {
      stock_id: stockId,
      order_type: orderType,
      order_price: parseFloat(orderPrice),
      quantity: parseInt(quantity)
    };

    try {
      const token = localStorage.getItem('token'); // Assuming token is stored here
      const res = await axios.post('/api/order', orderData, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setMessage('✅ Order created successfully!');
    } catch (err) {
      console.error('❌ Order creation failed:', err);
      setMessage('❌ Failed to create order. Check console for details.');
    }
  };

  return (
    <Paper style={{ padding: 20, maxWidth: 500, margin: 'auto' }}>
      <Typography variant="h5" gutterBottom>Create Trade Order</Typography>
      <form onSubmit={handleSubmit}>
        <TextField
          fullWidth
          select
          label="Select Stock"
          value={stockId}
          onChange={(e) => setStockId(e.target.value)}
          margin="normal"
          required
        >
          {stocks.map((stock) => (
            <MenuItem key={stock.id} value={stock.id}>
              {stock.symbol} - {stock.name}
            </MenuItem>
          ))}
        </TextField>
        <TextField
          fullWidth
          select
          label="Order Type"
          value={orderType}
          onChange={(e) => setOrderType(e.target.value)}
          margin="normal"
        >
          <MenuItem value="buy">Buy</MenuItem>
          <MenuItem value="sell">Sell</MenuItem>
        </TextField>
        <TextField
          fullWidth
          label="Order Price"
          type="number"
          value={orderPrice}
          onChange={(e) => setOrderPrice(e.target.value)}
          margin="normal"
          required
        />
        <TextField
          fullWidth
          label="Quantity"
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          margin="normal"
          required
        />
        <Button
          type="submit"
          variant="contained"
          color="primary"
          style={{ marginTop: 20 }}
        >
          Submit Order
        </Button>
        {message && (
          <Typography style={{ marginTop: 10, color: message.includes('✅') ? 'green' : 'red' }}>
            {message}
          </Typography>
        )}
      </form>
    </Paper>
  );
}
