import { useState } from 'react';
import { TextField, Button, Box, MenuItem, Paper, Typography } from '@mui/material';
import axios from '../../api/axios';

export default function CreateOrderForm({ onCreated }) {
  const [formData, setFormData] = useState({
    symbol: '', qty: '', type: 'Buy', price: ''
  });

  const handleChange = e => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    await axios.post('/orders', formData);
    setFormData({ symbol: '', qty: '', type: 'Buy', price: '' });
    onCreated?.();
  };

  return (
    <Paper sx={{
      p: 4,
      backgroundColor: '#112D4E',
      color: '#e0f7fa',
      borderRadius: 3,
      boxShadow: '0 0 12px rgba(0,255,170,0.2)',
      maxWidth: 400,
      mx: 'auto',
      mt: 4
    }}>
      <Typography variant="h6" sx={{
        color: '#00ffc6',
        fontWeight: 'bold',
        textShadow: '0 0 4px #00ffc6',
        mb: 2,
        textAlign: 'center'
      }}>
        Create New Order
      </Typography>

      <form onSubmit={handleSubmit}>
        <TextField fullWidth label="Symbol" name="symbol" value={formData.symbol}
          onChange={handleChange} margin="normal" sx={inputStyle} required />
        <TextField fullWidth label="Quantity" name="qty" value={formData.qty}
          onChange={handleChange} margin="normal" sx={inputStyle} required />
        <TextField select fullWidth label="Type" name="type" value={formData.type}
          onChange={handleChange} margin="normal" sx={inputStyle}>
          <MenuItem value="Buy">Buy</MenuItem>
          <MenuItem value="Sell">Sell</MenuItem>
        </TextField>
        <TextField fullWidth label="Price" name="price" value={formData.price}
          onChange={handleChange} margin="normal" sx={inputStyle} required />

        <Button type="submit" fullWidth variant="contained"
          sx={{
            mt: 3,
            backgroundColor: '#00ffc6',
            color: '#0f2027',
            fontWeight: 'bold',
            '&:hover': {
              backgroundColor: '#00e3b2',
              boxShadow: '0 0 8px #00e3b2',
            }
          }}>
          Submit
        </Button>
      </form>
    </Paper>
  );
}

const inputStyle = {
  '& .MuiInputBase-input': { color: '#e0f7fa' },
  '& .MuiInputLabel-root': { color: '#00ffc6' },
  '& .MuiOutlinedInput-root': {
    '& fieldset': { borderColor: '#00ffc6' },
    '&:hover fieldset': {
      borderColor: '#00e3b2',
      boxShadow: '0 0 6px #00ffc6'
    },
    '&.Mui-focused fieldset': { borderColor: '#00ffc6' }
  }
};
