import React, { useEffect, useState } from 'react';
import axios from '../../api/axios';
import {
  Table,
  TableHead,
  TableBody,
  TableRow,
  TableCell,
  Paper,
  Typography,
  Box,
  CircularProgress,
} from '@mui/material';

const PortfolioScreen = () => {
  const [portfolio, setPortfolio] = useState([]);
  const [summary, setSummary] = useState({});
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  const fetchPortfolio = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('User not authenticated. Please log in.');
      setLoading(false);
      return;
    }

    try {
      setLoading(true);
      const res = await axios.get('/portfolio', {
        headers: { Authorization: `Bearer ${token}` },
      });

      const { portfolio: portfolioData, summary: summaryData } = res.data;

      if (Array.isArray(portfolioData)) {
        setPortfolio(portfolioData);
        setSummary(summaryData || {});
        setError('');
      } else {
        setPortfolio([]);
        setSummary({});
        setError('Portfolio data is missing or invalid from API.');
      }
    } catch (err) {
      console.error('Error fetching portfolio:', err);
      setError('Failed to fetch portfolio data.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPortfolio();
    const interval = setInterval(fetchPortfolio, 6000);
    return () => clearInterval(interval);
  }, []);

  return (
    <Box p={3}>
      <Typography variant="h5" gutterBottom>
        Virtual Portfolio
      </Typography>

      {loading ? (
        <CircularProgress />
      ) : error ? (
        <Typography color="error">{error}</Typography>
      ) : (
        <>
          <Paper sx={{ overflowX: 'auto' }}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Symbol</TableCell>
                  <TableCell>Qty</TableCell>
                  <TableCell>Avg Buy Price</TableCell>
                  <TableCell>Current Price</TableCell>
                  <TableCell>Market Value</TableCell>
                  <TableCell>Unrealized P/L</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {portfolio.map((row, index) => (
                  <TableRow key={index}>
                    <TableCell>{row.symbol}</TableCell>
                    <TableCell>{row.qty}</TableCell>
                    <TableCell>${row.avgBuyPrice?.toFixed(2) ?? '0.00'}</TableCell>
                    <TableCell>${row.currentPrice?.toFixed(2) ?? '0.00'}</TableCell>
                    <TableCell>${row.marketValue?.toFixed(2) ?? '0.00'}</TableCell>
                    <TableCell style={{ color: row.unrealizedPL >= 0 ? 'green' : 'red' }}>
                      ${row.unrealizedPL?.toFixed(2) ?? '0.00'}
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>

            </Table>
          </Paper>
          <Box mt={3}>
            <Typography>
              <strong>Total Invested:</strong> ${summary.totalInvested ? Number(summary.totalInvested).toFixed(2) : '0.00'}
            </Typography>
            <Typography>
              <strong>Market Value:</strong> ${parseFloat(summary.totalMarketValue ? Number(summary.totalMarketValue).toFixed(2) : '0.00')}
            </Typography>
            <Typography style={{ color: Number(summary.totalPL) >= 0 ? 'green' : 'red' }}>
              <strong>Total P/L:</strong> ${summary.totalPL ? Number(summary.totalPL).toFixed(2) : '0.00'}
            </Typography>
          </Box>
        </>
      )}
    </Box>
  );
};

export default PortfolioScreen;
