import React, { useState } from 'react';
import axios from '../../api/axios';
import 
{ 
  Button, 
  Typography, 
  Paper, 
  CircularProgress, 
  Alert 
} from '@mui/material';
export default function CsvStockUploader() 
{
  const [file, setFile] = useState(null);
  const [parsedData, setParsedData] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);
  const handleFileChange = (e) => 
  {
    setFile(e.target.files[0]);
    setMessage(null);
    setError(null);
  };
  const handleUpload = async () => 
  {
    const formData = new FormData();
    formData.append('file', file);
    try 
    {
      const res = await axios.post('/api/stocks/upload', formData, 
      {
        headers: 
        {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`
        }
      });
    setMessage('Upload successful');
  } catch (err) {
    console.error(err);
    setError('Upload failed'+err);
  }
};

  return (
    <Paper elevation={3} style={{ padding: '20px', margin: '20px' }}>
      <Typography variant="h6">Upload CSV Stock Data</Typography>
      <input type="file" accept=".csv" onChange={handleFileChange} />
      <Button
        onClick={handleUpload}
        variant="contained"
        style={{ marginTop: '10px' }}
        disabled={!file || uploading}
      >
        {uploading ? 'Uploading...' : 'Upload'}
      </Button>
      {uploading && <CircularProgress style={{ marginLeft: '10px' }} />}
      {message && <Alert severity="success" style={{ marginTop: '10px' }}>{message}</Alert>}
      {error && <Alert severity="error" style={{ marginTop: '10px' }}>{error}</Alert>}
    </Paper>
  );
}
