import React, { useState } from 'react';
import axios from 'axios';

const OrderForm = () => {
  const [stock_id, setStockId] = useState('');
  const [order_type, setOrderType] = useState('buy');
  const [order_price, setOrderPrice] = useState('');
  const [quantity, setQuantity] = useState('');
  const [message, setMessage] = useState(''); // ✅ This line was missing

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/orders', {
        stock_id,
        order_type,
        order_price,
        quantity
      }, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`
        }
      });

      console.log("Order placed:", response.data);
      setMessage("Order placed successfully!");
    } catch (error) {
      console.error("Order creation failed:", error);
      setMessage("Order creation failed: " + (error?.response?.data?.message || error.message));
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Place Trade Order</h2>

      <input
        type="text"
        placeholder="Stock ID"
        value={stock_id}
        onChange={(e) => setStockId(e.target.value)}
        required
      />

      <select
        value={order_type}
        onChange={(e) => setOrderType(e.target.value)}
      >
        <option value="buy">Buy</option>
        <option value="sell">Sell</option>
      </select>

      <input
        type="number"
        placeholder="Order Price"
        value={order_price}
        onChange={(e) => setOrderPrice(e.target.value)}
        required
      />

      <input
        type="number"
        placeholder="Quantity"
        value={quantity}
        onChange={(e) => setQuantity(e.target.value)}
        required
      />

      <button type="submit">Place Order</button>

      {message && <p>{message}</p>}
    </form>
  );
};

export default OrderForm;
