import React, { useState } from 'react';
import axios from '../../api/axios';
import 
{ 
  Paper, 
  Typography, 
  Button, 
  CircularProgress, 
  Alert 
} from '@mui/material';
export default function CsvStockUploader() 
{
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);
  const handleFileChange = (e) => 
  {
    setFile(e.target.files[0]);
    setMessage(null);
    setError(null);
  };
  const handleUpload = async () => 
  {
    if (!file) 
    {
      setError('Please select a file first');
      return;
    }
    const formData = new FormData();
    formData.append('file', file);
    setUploading(true);
    setMessage(null);
    setError(null);

    try {
      const res = await axios.post('/stocks/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      setMessage(res.data.message || 'Upload successful');
      setFile(null); // optional: clear file input after success
    } catch (err) {
      console.error(err);
      setError('Upload failed: ' + (err?.response?.data?.error || err.message));
    } finally {
      setUploading(false);
    }
  };

  return (
    <Paper sx={{ padding: 3, maxWidth: 500, margin: 'auto', marginTop: 4 }}>
      <Typography variant="h6" gutterBottom>Upload CSV Stock File</Typography>
      <input type="file" accept=".csv" onChange={handleFileChange} />
      <Button 
        variant="contained" 
        onClick={handleUpload} 
        disabled={uploading || !file}
        sx={{ mt: 2 }}
      >
        {uploading ? <CircularProgress size={24} /> : 'Upload'}
      </Button>
      {message && <Alert severity="success" sx={{ mt: 2 }}>{message}</Alert>}
      {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
    </Paper>
  );
}
