import React, { useEffect, useState } from 'react';
import axios from '../../api/axios';
import 
{ 
  Table, 
  TableHead, 
  TableRow,
  TableCell, 
  TableBody, 
  Paper, 
  Typography, 
  CircularProgress 
} from '@mui/material';
export default function PortfolioScreen() {
  const [portfolio, setPortfolio] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPortfolio = async () => {
      try {
        const response = await axios.get('/portfolio'); // ✅ Adjust API path if needed
        let data = response.data;

        // ✅ Check if data is nested like: { portfolio: [...] }
        if (!Array.isArray(data)) {
          data = data.portfolio || []; // fallback
        }

        const formattedData = data.map((item) => {
          const avgBuyPrice = parseFloat(item.avgBuyPrice) || 0;
          const marketValue = parseFloat(item.marketValue) || 0;
          const quantity = parseFloat(item.qty) || 0;
          const buyValue = avgBuyPrice * quantity;
          const profitLoss = marketValue - buyValue;

          return {
            ...item,
            avgBuyPrice,
            marketValue,
            quantity,
            profitLoss,
          };
        });

        setPortfolio(formattedData);
      } catch (error) {
        console.error('Failed to load portfolio', error);
        setPortfolio([]);
      } finally {
        setLoading(false);
      }
    };

    fetchPortfolio();
  }, []);

  if (loading) return <CircularProgress />;

  return (
    <Paper style={{ padding: 20 }}>
      <Typography variant="h5" gutterBottom>
        Portfolio
      </Typography>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Symbol</TableCell>
            <TableCell>Quantity</TableCell>
            <TableCell>Avg Buy Price</TableCell>
            <TableCell>Market Value</TableCell>
            <TableCell>Profit / Loss</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {portfolio.map((row, index) => (
            <TableRow key={index}>
              <TableCell>{row.symbol}</TableCell>
              <TableCell>{row.quantity}</TableCell>
              <TableCell>{row.avgBuyPrice.toFixed(2)}</TableCell>
              <TableCell>{row.marketValue.toFixed(2)}</TableCell>
              <TableCell
                style={{ color: row.profitLoss >= 0 ? 'green' : 'red' }}
              >
                {row.profitLoss.toFixed(2)}
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </Paper>
  );
}
