import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TrendingStocks = () => {
  const [trendingData, setTrendingData] = useState([]);

  useEffect(() => {
    const fetchTrending = async () => {
      try {
        const res = await axios.get('/api/stocks/trending');
        setTrendingData(res.data);
      } catch (err) {
        console.error('Failed to fetch trending stocks', err);
      }
    };
    fetchTrending();
  }, []);

  return (
    <div style={styles.container}>
      <h2 style={styles.header}>ðŸ”¥ Trending Stocks</h2>
      <div style={styles.grid}>
        {trendingData.map((stock) => (
          <div key={stock.symbol} style={styles.card}>
            <h3>{stock.symbol}</h3>
            <p><strong>Price:</strong> ${stock.price}</p>
            <p>
              <strong>Change:</strong>{' '}
              <span style={{ color: stock.change >= 0 ? 'green' : 'red' }}>
                {stock.change.toFixed(2)} ({stock.percent}%)
              </span>
            </p>
            <p><strong>High:</strong> ${stock.high}</p>
            <p><strong>Low:</strong> ${stock.low}</p>
          </div>
        ))}
      </div>
    </div>
  );
};
