import { useState } from 'react';
import { TextField, Button, Paper, Typography, Snackbar, Alert } from '@mui/material';
import axios from '../../api/axios';

export default function CustomerForm() {
  const [formData, setFormData] = useState({
    full_name: '',
    email: '',
    phone: '',
    cnic: '',
    customer_no: '',
  });

  const [error, setError] = useState(null);
  const [successOpen, setSuccessOpen] = useState(false);

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/customers', formData);
      setSuccessOpen(true);
      setFormData({ full_name: '', email: '', phone: '', cnic: '', customer_no: '' });
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to create customer');
    }
  };

  return (
    <Paper
      elevation={4}
      sx={{
        p: 4,
        mt: 3,
        backgroundColor: '#222831',
        color: '#f8f8f8',
        boxShadow: '0 0 12px rgba(0, 255, 170, 0.2)',
        borderRadius: 3,
      }}
    >
      <Typography
        variant="h6"
        gutterBottom
        sx={{
          color: '#00ffc6',
          fontWeight: 'bold',
          textShadow: '0 0 4px #00ffc6',
          mb: 2,
        }}
      >
        Create Customer
      </Typography>
      <form onSubmit={handleSubmit} noValidate>
        <TextField
          fullWidth
          label="Full Name"
          name="full_name"
          value={formData.full_name}
          onChange={handleChange}
          margin="normal"
          required
          sx={inputStyle}
        />
        <TextField
          fullWidth
          label="Email"
          name="email"
          type="email"
          value={formData.email}
          onChange={handleChange}
          margin="normal"
          required
          sx={inputStyle}
        />
        <TextField
          fullWidth
          label="Phone Number"
          name="phone"
          value={formData.phone}
          onChange={handleChange}
          margin="normal"
          sx={inputStyle}
        />
        <TextField
          fullWidth
          label="CNIC or ID Number"
          name="cnic"
          value={formData.cnic}
          onChange={handleChange}
          margin="normal"
          required
          sx={inputStyle}
        />
        <TextField
          fullWidth
          label="Customer No"
          name="customer_no"
          value={formData.customer_no}
          onChange={handleChange}
          margin="normal"
          required
          sx={inputStyle}
        />
        <Button
          type="submit"
          variant="contained"
          sx={{
            mt: 3,
            backgroundColor: '#00ffc6',
            color: '#0f2027',
            fontWeight: 'bold',
            '&:hover': {
              backgroundColor: '#00e3b2',
            },
          }}
        >
          Save Customer
        </Button>
      </form>
      <Snackbar open={successOpen} autoHideDuration={4000} onClose={() => setSuccessOpen(false)}>
        <Alert severity="success" onClose={() => setSuccessOpen(false)}>
          Customer created successfully!
        </Alert>
      </Snackbar>
      {error && (
        <Alert severity="error" sx={{ mt: 2 }}>
          {error}
        </Alert>
      )}
    </Paper>
  );
}

const inputStyle = {
  '& .MuiInputBase-input': {
    color: '#e0f7fa',
  },
  '& .MuiInputLabel-root': {
    color: '#00ffc6',
  },
  '& .MuiOutlinedInput-root': {
    '& fieldset': {
      borderColor: '#00ffc6',
    },
    '&:hover fieldset': {
      borderColor: '#00e3b2',
    },
    '&.Mui-focused fieldset': {
      borderColor: '#00ffc6',
    },
  },
};
