import 
{ 
  useState, 
  useEffect 
} from 'react';
import 
{
  Paper,
  Typography,
  TableContainer,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Button,
  Alert,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions
} from '@mui/material';
import axios from '../../api/axios';
export default function MarketWatch() 
{
  const [stocks, setStocks] = useState([]);
  const [watchlist, setWatchlist] = useState(() => 
  {
    const saved = localStorage.getItem('watchlist');
    return saved ? JSON.parse(saved) : [];
  });
  const [message, setMessage] = useState('');
  const [cartOpen, setCartOpen] = useState(false);
  useEffect(() => 
  {
    fetchStocks();
  }, []);
  const fetchStocks = async () => 
  {
    try 
    {
      const res = await axios.get('/stocks');
      setStocks(res.data);
    } 
    catch (err) 
    {
      console.error('Error fetching stocks:', err);
    }
  };
  const addToWatchlist = (symbol) => 
  {
    if (watchlist.includes(symbol)) 
    {
      setMessage(`"${symbol}" is already in your cart.`);
      return;
    }
    const updatedList = [...watchlist, symbol];
    setWatchlist(updatedList);
    localStorage.setItem('watchlist', JSON.stringify(updatedList));
    setMessage(`"${symbol}" âœ… added to your cart.`);
  };
  function getChangeColor (openprice, closeprice) 
  {
    const open = parseInt(stocks.openprice);
    const close = parseInt(stocks.closeprice);
    const isValidOpen = !isNaN(open) && open !== 0;
    const isValidClose = !isNaN(close);
    const changePercent = isValidOpen && isValidClose
      ? (((close - open) / open) * 100).toFixed(2) : null;
    if (changePercent > 0) return 'green';
    if (changePercent < 0) return 'red';
    console.log(changePercent);
    return 'inherit';
  };
  const cartStocks = stocks.filter(stock => watchlist.includes(stock.symbol));
  return (
    <>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography
          variant="h5"
          mt={4}
          mb={2}
          sx={{
            textAlign: "center",
            background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
            borderRadius: 2,
            boxShadow: '0 0 12px rgba(0, 255, 170, 0.2)',
            padding: "10px",
            fontWeight: "bold",
            color: '#00ffcc',
            textShadow: '0 0 3px #00ffcc',
            flex: 1
          }}
        >
          ðŸ“ˆ Market Watch
        </Typography>
        <Button
          variant="contained"
          sx={{
            textAlign: "center",
            background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
            borderRadius: 2,
            boxShadow: '0 0 12px rgba(0, 255, 170, 0.2)',
            height: '60px',
            mt: 2,
            ml: 2
          }}
          onClick={() => setCartOpen(true)}
        >
          ðŸ›’ Cart ({watchlist.length})
        </Button>
      </div>
      {message && (
        <Alert severity="info" sx={{ mb: 2 }}>
          {message}
        </Alert>
      )}
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><strong>Symbol</strong></TableCell>
              <TableCell><strong>Name</strong></TableCell>
              <TableCell><strong>Last Price</strong></TableCell>
              <TableCell><strong>Change %</strong></TableCell>
              <TableCell><strong>Volume</strong></TableCell>
              <TableCell><strong>Watchlist</strong></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {stocks.map((stock) => 
            {
              const open = parseInt(stocks.openprice);
              const close = parseInt(stocks.closeprice);
              return (
                <TableRow key={stock.symbol}>
                  <TableCell>{stock.symbol}</TableCell>
                  <TableCell>{stock.companyname || "-"}</TableCell>
                  <TableCell>
                    {!isNaN(close) ? close.toFixed(2) : "-"}
                  </TableCell>
                  <TableCell sx={{ color: getChangeColor(open, close) }}>
                    {changePercent}%
                  </TableCell>
                  <TableCell>{stock.volume ?? 0}</TableCell>
                  <TableCell>
                    <Button
                      variant="outlined"
                      size="small"
                      disabled={watchlist.includes(stock.symbol)}
                      onClick={() => addToWatchlist(stock.symbol)}
                    >
                      {watchlist.includes(stock.symbol) ? 'Added âœ…' : 'Add to cart ðŸ›’'}
                    </Button>
                  </TableCell>
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </TableContainer>
      <Dialog open={cartOpen} onClose={() => setCartOpen(false)} fullWidth maxWidth="sm">
        <DialogTitle>Your Cart ðŸ›’</DialogTitle>
        <DialogContent>
          {cartStocks.length > 0 ? (
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell><strong>Symbol</strong></TableCell>
                  <TableCell><strong>Name</strong></TableCell>
                  <TableCell><strong>Last Price</strong></TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {cartStocks.map((stock) => (
                  <TableRow key={stock.symbol}>
                    <TableCell>{stock.symbol}</TableCell>
                    <TableCell>{stock.companyname || "-"}</TableCell>
                    <TableCell>
                      {stock.closeprice ? parseFloat(stock.closeprice).toFixed(2) : "-"}
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          ) : (
            <Typography>No stocks in your cart yet.</Typography>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setCartOpen(false)}>Close</Button>
        </DialogActions>
      </Dialog>
    </>
  );
}
