import React, { useEffect, useState, Styl } from 'react';

const TrendingStocks = () => {
  const [trendingData, setTrendingData] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/stocks/trending') // Adjust URL as needed
      .then(res => res.json())
      .then(data => setTrendingData(data))
      .catch(err => console.error('Failed to fetch trending stocks:', err));
  }, []);

  return (
    <div style={styles.container}>
      <h2 style={styles.header}>üî• Trending Stocks</h2>
      <div style={styles.grid}>
        {trendingData.map((stock, index) => (
          <div key={index} style={styles.card}>
            <p><strong>üè≠ Company Name:</strong> {stock.CompanyName}</p>
            <p><strong>üì≠ Open Price:</strong> {stock.OpenPrice}</p>
            <p><strong>üìà High Price:</strong> {stock.HighPrice}</p>
            <p><strong>üìâ Low Price:</strong> {stock.LowPrice}</p>
            <p><strong>üì™ Close Price:</strong> {stock.ClosePrice}</p>
            <p><strong>üîí Previous Close:</strong> {stock.PreviousClose}</p>
            <p><strong>üóëÔ∏è Volume:</strong> {stock.Volume}</p>
          </div>
        ))}
      </div>
    </div>
  );
};
export default TrendingStocks;
