import React, { useEffect, useState } from 'react';
import axios from 'axios';
import
{ 
  Table, 
  TableHead, 
  TableBody, 
  TableRow, 
  TableCell, 
  Paper, Typography, Box } from '@mui/material';
const PortfolioScreen = () => {
  const [portfolio, setPortfolio] = useState([]);
  const [summary, setSummary] = useState({});

  const fetchPortfolio = async () => {
  try {
    const token = localStorage.getItem('token'); // Get the JWT
    const res = await axios.get('/api/portfolio', {
      headers: {
        Authorization: `Bearer ${token}`
      }

    });
    setPortfolio(res.data.portfolio);
    setSummary(res.data.summary);
  } catch (err) {
    console.error('Failed to fetch portfolio:', err);
  }
};


  useEffect(() => {
    fetchPortfolio();
    const interval = setInterval(fetchPortfolio, 6000);
    return () => clearInterval(interval);
  }, []);

  return (
    <Box p={3}>
      <Typography variant="h5" gutterBottom>Virtual Portfolio</Typography>
      <Paper>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Symbol</TableCell>
              <TableCell>Qty</TableCell>
              <TableCell>Avg Buy Price</TableCell>
              <TableCell>Current Price</TableCell>
              <TableCell>Market Value</TableCell>
              <TableCell>Unrealized P/L</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {portfolio.map((row, i) => (
              <TableRow key={i}>
                <TableCell>{row.symbol}</TableCell>
                <TableCell>{row.qty}</TableCell>
                <TableCell>${row.avgBuyPrice}</TableCell>
                <TableCell>${row.currentPrice}</TableCell>
                <TableCell>${row.marketValue}</TableCell>
                <TableCell
                  style={{ color: row.unrealizedPL >= 0 ? 'green' : 'red' }}
                >
                  ${row.unrealizedPL}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>

      <Box mt={3}>
        <Typography><strong>Total Invested:</strong> ${summary.totalInvested}</Typography>
        <Typography><strong>Market Value:</strong> ${summary.totalMarketValue}</Typography>
        <Typography style={{ color: summary.totalPL >= 0 ? 'green' : 'red' }}>
          <strong>Total P/L:</strong> ${summary.totalPL}
        </Typography>
      </Box>
    </Box>
  );
};

export default PortfolioScreen;
