import React, { useState } from 'react';
import axios from '../../api/axios';
import { Paper, Typography, Button, CircularProgress, Alert } from '@mui/material';

export default function CsvStockUploader() {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setMessage(null);
    setError(null);
  };

  const handleUpload = async () => {
    if (!file) {
      setError('Please select a file first');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    setUploading(true);

    try {
      const response = await axios.post('/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setMessage(response.data.message);
      setError(null);
    } catch (err) {
      setError(err.response?.data?.message || 'Upload failed: ' + err.message);
      setMessage(null);
    } finally {
      setUploading(false);
    }
  };

  return (
    <Paper
      elevation={4}
      sx={{
        p: 4,
        mt: 3,
        backgroundColor: '#112D4E',
        color: '#e0f7fa',
        borderRadius: 3,
        boxShadow: '0 0 12px rgba(0, 255, 170, 0.25)',
        maxWidth: '460px',
        margin: '0 auto',
      }}
    >
      <Typography
        variant="h6"
        gutterBottom
        sx={{
          color: '#00ffc6',
          fontWeight: 'bold',
          textAlign: 'center',
          textShadow: '0 0 4px #00ffc6',
          mb: 2,
        }}
      >
        Upload CSV File
      </Typography>

      <input
        type="file"
        accept=".csv"
        onChange={handleFileChange}
        style={{
          backgroundColor: '#0f3460',
          color: '#e0f7fa',
          padding: '10px',
          borderRadius: '6px',
          border: '1px solid #00ffc6',
          width: '100%',
          marginBottom: '20px',
        }}
      />

      <Button
        variant="contained"
        disabled={uploading}
        onClick={handleUpload}
        sx={{
          width: '100%',
          backgroundColor: '#00ffc6',
          color: '#0f2027',
          fontWeight: 'bold',
          py: 1.2,
          transition: 'all 0.3s ease',
          '&:hover': {
            backgroundColor: '#00e3b2',
            boxShadow: '0 0 8px #00e3b2',
          },
        }}
      >
        {uploading ? <CircularProgress size={24} color="inherit" /> : 'Upload'}
      </Button>

      <br /><br />
      {message && <Alert severity="success">{message}</Alert>}
      {error && <Alert severity="error">{error}</Alert>}
    </Paper>
  );
}
